function burgerMenu(){const e=document.querySelector(".burger"),t=document.querySelector(".header__navigation"),s=document.querySelector(".overlay");e.addEventListener("click",()=>{e.classList.toggle("active"),t.classList.toggle("active"),s.classList.toggle("active")}),s.addEventListener("click",()=>{e.classList.remove("active"),t.classList.remove("active"),s.classList.remove("active")}),window.addEventListener("resize",()=>{991.98<window.innerWidth&&(e.classList.remove("active"),t.classList.remove("active"),s.classList.remove("active"))})}burgerMenu();